Default.aspx.cs

using TEC.Core.WebForm.Extensions;
using TEC.Core.WebForm.Net;
using TEC.Core.WebForm.Storage;
public partial class _Default : System.Web.UI.Page
{
    protected void someMethod(string someParameter)
    {
        //Uri建立器
         UriCreator creator = new UriCreator();
        Dictionary<string, string> parameter = new Dictionary<string, string>();
        parameter.Add("someParameterName", someParameter);
        //使用預先加入頁面的方法
        PassedPageExtension.preAddPage(new HtmlInformationStorage(creator.generateUri("~/Default2.aspx"), creator.generateUri("~/Default2.aspx"), parameter));
        //導向到另一頁面時，再取用該頁參數即可
        System.Web.HttpContext.Current.Response.Redirect("~/Default2.aspx");
    }
}

