example.code:
public override string GetDesignTimeHtml()
{
  return renderDesignTimeCss(
      "TEC.Core.WebForm.WebControls.AJAX.ProgressBarControl._assets.css.progress.css") +
      base.GetDesignTimeHtml();
}

