remarks.example:
 使用lock()控制流程之範例 OnlineMemberList.cs

  public class OnlineMemberList : IOnlineMemberList<OnlineMemberStorage, Guid>
{
    private static readonly object sync_obj = new object();
    private List<OnlineMemberStorage> currentList;

    public OnlineMemberList()
    {
        this.currentList = new List<OnlineMemberStorage>();
    }
    public int IndexOf(OnlineMemberStorage item)
    {
        return this.currentList.IndexOf(item);
    }
    public void Insert(int index, OnlineMemberStorage item)
    {
        lock (sync_obj)
        {
            this.currentList.Insert(index, item);
        }
    }
    public void RemoveAt(int index)
    {
        lock (sync_obj)
        {
            this.currentList.RemoveAt(index);
        }
    }
    //省略部分程式碼
}

