  </configSections>
  <onlineMember idleToOfflineMinute="20" refreshIntervalSecond="30" />
  <system.webServer>
    <modules runAllManagedModulesForAllRequests="true">
      <add name="OnlineMemberModule" type="TEC.Core.Web.Modules.OnlineMember.OnlineMemberModule,TEC.Core.Web"/>
    </modules>
  </system.webServer>
</configuration>



 OnlineMemberStorage.cs


 public class OnlineMemberStorage : IOnlineMemberStorage<Guid>
{
    public Guid Key { set; get; }
    public Guid OnlineMemberKey
    {
        get { return this.Key; }
    }
    public DateTime LastConnectTime { get; set; }
    public TimeSpan IdleTimeSpan { get; set; }
    public object Clone()
    {
        return this.MemberwiseClone();
    }
    public override bool Equals(object obj)
    {
        if (!(obj is OnlineMemberStorage))
        {
            return false;
        }
        return ((OnlineMemberStorage)obj).OnlineMemberKey == this.OnlineMemberKey;
    }
    public override int GetHashCode()
    {
        return this.OnlineMemberKey.GetHashCode();
    }
    public static bool operator ==(OnlineMemberStorage onlineMemberStorage1, OnlineMemberStorage onlineMemberStorage2)
    {
        return onlineMemberStorage1.Equals(onlineMemberStorage2);
    }
    public static bool operator !=(OnlineMemberStorage onlineMemberStorage1, OnlineMemberStorage onlineMemberStorage2)
    {
        return !(onlineMemberStorage1.Equals(onlineMemberStorage2));
    }
}



