 Global.asax


  public class Global : System.Web.HttpApplication
{
    protected void Application_Start(object sender, EventArgs e)
    {
        OnlineMemberModule.registerOnlineMemberInformationProvider<Guid>(new OnlineMemberInformationProvider());
        //若您要使用預設的記憶體資料來源，可以使用TEC.Core.Web.Modules.OnlineMember.OnlineMemberMemoryDataSource，且也不須實作IOnlineMemberRequiresDataSource介面
        //使用預設資料來源範例如下:
        //OnlineMemberModule.registerOnlineMemberDataSource<OnlineMemberStorage, Guid>(new OnlineMemberMemoryDataSource<OnlineMemberStorage, Guid>(new OnlineMemberList()));
        OnlineMemberModule.registerOnlineMemberDataSource<OnlineMemberStorage, Guid>(new OnlineMemberDataSource());
    }
    protected void Application_End(object sender, EventArgs e)
    {
        OnlineMemberModule.unregisterOnlineMemberDataSource();
        OnlineMemberModule.unregisterOnlineMemberInformationProvider();
    }
}

