public void PTL00205OrderQuery()
{
    OrderQuerySendInfo orderQuerySendInfo = new OrderQuerySendInfo("Service@tecyt.com", "85971456",
     this.Url.Action("PTL00206OrderQueryReceived", "PTL00200EzShip", null, this.Request.Url.Scheme));
    var webRequest = System.Net.WebRequest.Create(orderQuerySendInfo.ActionUrl) as System.Net.HttpWebRequest;
    if (webRequest != null)
    {
        byte[] dataStream = System.Text.Encoding.GetEncoding("Big5").GetBytes(orderQuerySendInfo.ToString());
        webRequest.KeepAlive = true;
        webRequest.CachePolicy = new System.Net.Cache.RequestCachePolicy(System.Net.Cache.RequestCacheLevel.BypassCache);
        webRequest.Method = "POST";
        webRequest.ContentType = "application/x-www-form-urlencoded";
        System.IO.Stream stream = webRequest.GetRequestStream();
        stream.Write(dataStream, 0, dataStream.Length);
        stream.Close();
        using (System.Net.WebResponse webResponse = webRequest.GetResponse())
        {
            // 送出Post後，ezShip將直接回傳資訊至指定的回傳網址中
            // 若您需攔截回覆資訊，請取消註解以下程式碼
            // ---
            // 取得傳回的資訊，若您有指定orderQuerySendInfo.rtn_url，則該位址也將收到相同資訊
            //OrderQueryReceivedInfo orderQueryReceivedInfo = OrderQueryReceivedInfo.parse(webResponse.ResponseUri.Query);
            // ---
            //System.IO.Stream responseStream = webResponse.GetResponseStream();
            //System.IO.StreamReader myStreamReader = new System.IO.StreamReader(responseStream, System.Text.Encoding.GetEncoding("Big5"));
            //pageContent是讀取網頁傳回內容，除非對方伺服器發生錯誤，除此值外都應為空白
            //string pageContent = myStreamReader.ReadToEnd();
            //myStreamReader.Close();
            //responseStream.Close();
        }
    }
}
