    orderMaster.rvName = "TestName";
    //收信用
    orderMaster.rvEmail = "TestAccount@tecyt.com";
    //收簡訊
    orderMaster.rvMobile = "0912111111";
    orderMaster.EzShipStCate = EzShipStCate.TFM;//全家
    orderMaster.EzShipStCode = "2659";//全家中和新平店
    orderMaster.rtURL = this.Url.Action("PTL00204OrderReceived", "PTL00200EzShip", null, this.Request.Url.Scheme);
    orderMaster.webPara = "CustomParameters";//會原值回傳
    orderMaster.DetailList = new List<OrderDetail>()
    {
        new OrderDetail()
        {
            prodItem=001,
            prodName="產品1",
            prodNo="PROD001",
            prodPrice=20,
            prodQty=5,
            prodSpec="產品1的描述"
        },
        new OrderDetail()
