    private void AllPayHelper_OnPostDataReceived(object sender, PostDataReceivedEventArgs e)
    {
        string rawData = e.RawParameterString;
        //這裡可以將原始歐付寶傳來的資訊紀錄至Log中
    }
    private void AllPayHelper_OnPostDataParseFailed(object sender, PostDataParseFailedEventArgs e)
    {
        //歐付寶傳來的資訊在處理時發生錯誤
        if (e.Exception is PostDataNotRecognizingException)
        {
            //當處理歐付寶傳回資訊發生辨識錯誤時的例外資訊
            PostDataNotRecognizingException postDataNotRecognizingException = e.Exception as PostDataNotRecognizingException;
            string message = e.Exception.Message;
            //使用postDataNotRecognizingException.RawData取得原始字串
            string rawString = postDataNotRecognizingException.RawData;
        }
        else if (e.Exception is PostDataParseFailedException)
        {
            //當處理歐付寶傳回資訊發生辨識錯誤時的例外資訊
            PostDataParseFailedException postDataParseFailedException = e.Exception as PostDataParseFailedException;
            string message = e.Exception.Message;
            string errorKey = postDataParseFailedException.Key;
            //已從Post字串解析出的索引鍵/值的字典
            Dictionary<string, string> parsedParameterDictionary = postDataParseFailedException.RawDataDictionary;
        }
        else if (e.Exception is CheckMacValueInvalidException)
        {
            //當CheckMacValue驗證失敗時引發(大小寫視為相同來比對)
            CheckMacValueInvalidException checkMacValueInvalidException = e.Exception as CheckMacValueInvalidException;
            string message = e.Exception.Message;
            //現在的
            string currentValue = checkMacValueInvalidException.CurrentValue;
            //預期的
            string expectedValue = checkMacValueInvalidException.ExpectedValue;
            //注意: 當發生此錯誤時有可能是資料已遭竄改
        }
    }
    private void AllPayHelper_OnDataNotTriggerAnyEvent(object sender, PostDataNotHandleEventArgs e)
    {
        //沒有引發任何事件，請做紀錄檔的處理
    }
    #endregion

[AllowAnonymous]
public void PTL99991PayPalNotify()
{
    TEC.Core.Web.OnlineTransaction.PayPal.PayPalNotifyHelper payPalNotifyHelper = new TEC.Core.Web.OnlineTransaction.PayPal.PayPalNotifyHelper(true);
    payPalNotifyHelper.Invalid += this.payPalNotifyHelper_Invalid;
    payPalNotifyHelper.Verified += this.payPalNotifyHelper_Verified;
    payPalNotifyHelper.process(this.HttpContext);
}
private void payPalNotifyHelper_Verified(object sender, TEC.Core.Web.OnlineTransaction.PayPal.NotifyCompleteEventArgs e)
{
   //確認 "payment_status" 為「已完成」(Completed)，因為系統也會為其他結果（如「待處理」(Pending) 或「失敗」(Failed)）發送交易狀態更新
   //檢查 "txn_id" 並非複製的識別碼，以防範詐騙者重複利用已完成的舊交易
   //證實 "receiver_email" 是你在 PayPal 帳戶註冊的電子郵件，以防止將付款寄送到詐騙者的帳戶
   //檢查其他交易詳細資料（如物品編號和價格），以確認價格未改變
   switch (e.PayPalNotifyInfo.payment_status.ToLower())
   {
       //The payment has been completed, and the funds have been added successfully to your account balance.
       case "completed":
       //The payment was denied. This happens only if the payment was previously pending because of one of the reasons listed for the pending_reason variable or the Fraud_Management_Filters_x variable.
       case "denied":
       //The payment has failed. This happens only if the payment was made from your customer's bank account
       case "failed":
       //This authorization has been voided
       case "voided":
       //A payment was reversed due to a chargeback or other type of reversal. 
       //The funds have been removed from your account balance and returned to the buyer. The reason for the reversal is specified in the ReasonCode element
       case "reversed":
       //This authorization has expired and cannot be captured.
       case "expired":
       // A reversal has been canceled. For example, you won a dispute with the customer, and the funds for the transaction that was reversed have been returned to you
       case "canceled_reversal":
       //You refunded the payment.
       case "refunded":
       //A German ELV payment is made using Express Checkout
       case "created":
       //The payment is pending. See pending_reason for more information
       case "pending":
       //A payment has been accepted
       case "processed":
           //處理相關資料
           break;
       default:
           throw new NotImplementedException(String.Format("尚未實作IPN payment status為「{0}」之處理方式。", e.PayPalNotifyInfo.payment_status.ToLower()));
   }
}
private void payPalNotifyHelper_Invalid(object sender, TEC.Core.Web.OnlineTransaction.PayPal.NotifyCompleteEventArgs e)
{
    //這裡需要做例外處理
}

