         //檢查其他交易詳細資料（如物品編號和價格），以確認價格未改變

        switch (e.PayPalNotifyInfo.payment_status.ToLower())
        {
            //The payment has been completed, and the funds have been added successfully to your account balance.
            case "completed":
            //The payment was denied. This happens only if the payment was previously pending because of one of the reasons listed for the pending_reason variable or the Fraud_Management_Filters_x variable.
            case "denied":
            //The payment has failed. This happens only if the payment was made from your customer's bank account
            case "failed":
            //This authorization has been voided
            case "voided":
            //A payment was reversed due to a chargeback or other type of reversal. 
            //The funds have been removed from your account balance and returned to the buyer. The reason for the reversal is specified in the ReasonCode element
            case "reversed":
            //This authorization has expired and cannot be captured.
            case "expired":
            // A reversal has been canceled. For example, you won a dispute with the customer, and the funds for the transaction that was reversed have been returned to you
            case "canceled_reversal":
            //You refunded the payment.
            case "refunded":
            //A German ELV payment is made using Express Checkout
            case "created":
            //The payment is pending. See pending_reason for more information
            case "pending":
            //A payment has been accepted
            case "processed":
                //處理相關資料
                break;
            default:
                throw new NotImplementedException(String.Format("尚未實作IPN payment status為「{0}」之處理方式。", e.PayPalNotifyInfo.payment_status.ToLower()));
        }
    }
